mapper.insert:
Insert into USERINFO
(
    USER_ID, PASSWORD, USER_NAME, USER_EMAIL, AGE
) values (
             #{user_id},#{password}, #{user_name}, #{user_email}, #{age}
         )

mapper.select:
select *
from USERINFO
where USER_ID = #{user_id} and PASSWORD = #{password}

mapper.update:
update USERINFO
set user_id = #{user_id}, user_email = #{user_email}, user_name = #{user_name}
where user_seq = #{user_seq}
